<div class="lightboxFLContent">
    <form action="">

        <a href="" class="button -facebook">
            FB login
        </a>

        <div class="lightboxFLContent__horizontalDivider"></div>

        <label for="flRegisterEmail">E-mail *</label>


        <input id="flRegisterEmail"
               type="text">


        <label for="flRegisterPass">Heslo *</label>


        <input id="flRegisterPass"
               type="password">


        <label for="flRegisterPassRepeat">Heslo znovu *</label>


        <input id="flRegisterPassRepeat"
               type="password">


        <label for="flRegisterName">Vaše jméno *</label>

        <input id="flRegisterName"
               type="text">

        <label for="flRegisterCountry">Země *</label>


        <select name="flRegisterCountry" id="flRegisterCountry">
            <option value="cz">Česká republika</option>
            <option value="sk">Slovenská republika</option>
            <option value="pol">Polsko</option>
            <option value="ger">Německo</option>
            <option value="eng">Velká británie</option>
        </select>


        <div class="button -green js-formSubmitter">
            Register
        </div>


        <div class="lightboxFLContent__footnote">
            * označuje povinné položky
        </div>


    </form>
</div>
