<div class="lightboxFLContent -textBlock">

    <div class="lightboxFLContent__title">Nahrání souborů</div>

    <div class="lightboxFLContent__grid -four">
        <div class="lightboxFLContent__grid__col">
            <a class="bigButton -darkGreen -icon -uploadPhoto ">
                <div class="bigButton__title">Images</div>
                <div class="bigButton__subTitle">JPG, GIF, PNG</div>
            </a>
        </div>

        <div class="lightboxFLContent__grid__col">
            <a class="bigButton -darkGreen -icon -uploadVideo">
                <div class="bigButton__title">Videos</div>
                <div class="bigButton__subTitle">YouTube</div>
            </a>
        </div>

        <div class="lightboxFLContent__grid__col">
            <a class="bigButton -darkGreen -icon -uploadPhoto ">
                <div class="bigButton__title">Audio</div>
                <div class="bigButton__subTitle">WAV, MP3</div>
            </a>
        </div>

        <div class="lightboxFLContent__grid__col">
            <a class="bigButton -darkGreen -icon -uploadDocument ">
                <div class="bigButton__title">Documents</div>
                <div class="bigButton__subTitle">DOC, PDF, ODT, TXT</div>
            </a>
        </div>

    </div>

</div>



